import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ThemeService {

    isDarkMode: boolean = false;

    toggleTheme(): void {
        this.isDarkMode = !this.isDarkMode;
    }

    ngOnInit(): void {
        console.log(window.localStorage.setItem('darkMode', JSON.stringify(this.isDarkMode)))
    }

}
