import { Component } from "@angular/core";
import { HttpClient } from '@angular/common/http';

@Component ({
    selector: 'app-blog',
    templateUrl: './blog.component.html'
})

export class BlogComponent {

    apiUrl = 'https://its-events.davide-mantovani.workers.dev/events'; // Inserisci l'URL dell'API qui
    responseData: any;

    constructor(private http: HttpClient) {}

    ngOnInit(): void {
      this.getDataFromApi();
    }

    getDataFromApi(): void {
      const endpoint = '/your-endpoint'; // L'endpoint specifico dell'API

      this.http.get<any>(`${this.apiUrl}${endpoint}`).subscribe(
        (response) => {
          this.responseData = response; // Assegna la risposta alla variabile responseData
          console.log('Risposta API:', this.responseData);
        },
        (error) => {
          console.error('Errore nella chiamata API:', error);
        }
      );
    }
}
